mapper.update:
UPDATE topic_submission
SET title = #{title},
    description = #{description},
    file_id = #{fileId}
WHERE topic_id = #{topicId}

mapper.select:
SELECT topic_id, title, description, file_id, review_opinion, review_status, leader_opinion, leader_status
FROM topic_submission
WHERE topic_id = #{topicId}

mapper.update:
